<template>
    <div class="admin-layout">
        <!-- 侧边栏 -->
        <aside class="sidebar">
            <div class="logo">
                <img src="../assets/logo.svg" alt="Logo">
                <span>Cheemms Happy</span>
            </div>
            <nav>
                <router-link v-for="nav in navigations" :key="nav.path" :to="nav.path" class="nav-item">
                    <i :class="nav.icon"></i>
                    <span>{{ nav.title }}</span>
                </router-link>
            </nav>
        </aside>
        <!-- 主内容区 -->
        <main class="main-content-container">
            <!-- 顶部导航 -->
            <el-container>
                <el-header class="header">
                    <div class="header-content">
                        <!-- 左侧面包屑 -->
                        <div class="left-section">
                            <el-breadcrumb separator="/">
                                <el-breadcrumb-item :to="{ path: '/' }">{{ t('common.home') }}</el-breadcrumb-item>
                                <el-breadcrumb-item>{{ currentRoute }}</el-breadcrumb-item>
                            </el-breadcrumb>
                        </div>
                        <!-- 右侧用户信息 -->
                        <div class="right-section">
                            <div class="action-bar">
                                <!-- 语言切换 -->
                                <div class="lang-switcher">
                                    <el-button :type="locale === 'zh' ? 'primary' : 'text'" size="small"
                                        @click="changeLanguage('zh')">中文</el-button>
                                    <el-button :type="locale === 'en' ? 'primary' : 'text'" size="small"
                                        @click="changeLanguage('en')">EN</el-button>
                                </div>
                                <!-- 用户信息 -->
                                <el-dropdown>
                                    <div class="user-info">
                                        <el-avatar :size="32"
                                            src="data:image/jpeg;base64," />
                                        <span class="username">cheems</span>
                                        <i class="el-icon-arrow-down"></i>
                                    </div>
                                    <template #dropdown>
                                        <el-dropdown-menu>
                                            <el-dropdown-item>{{ t('common.profile') }}</el-dropdown-item>
                                            <el-dropdown-item @click="handleLogout">{{ t('common.logout')
                                                }}</el-dropdown-item>
                                        </el-dropdown-menu>
                                    </template>
                                </el-dropdown>
                            </div>
                        </div>
                    </div>
                </el-header>
                <!-- 内容区域 -->
                <div class="main-content">
                    <router-view v-slot="{ Component }">
                        <transition name="fade" mode="out-in">
                            <component :is="Component" />
                        </transition>
                    </router-view>
                </div>
            </el-container>
        </main>
    </div>
</template>
<script setup>
import { ref, computed } from 'vue'
import { useRouter, useRoute } from 'vue-router'
import { useI18n } from 'vue-i18n'

const { t } = useI18n()

const router = useRouter()
const route = useRoute()
const { locale } = useI18n()

const navigations = [
    { path: '/admin/dashboard', title: t('dashboard.dashboard'), icon: 'el-icon-data-line' },
    { path: '/admin/orders', title: t('order.orders'), icon: 'el-icon-document' },
    { path: '/admin/merchants', title: t('merchants.merchants'), icon: 'el-icon-shopping-bag-1' },
    { path: '/admin/merchants-api', title: t('merchantsApi.merchantsApi'), icon: 'el-icon-setting' },
    { path: '/admin/wallets', title: t('wallet.wallets'), icon: 'el-icon-wallet' },
    { path: '/admin/system-settings', title: t('systemSettings.systemSettings'), icon: 'el-icon-setting' },
]

const currentRoute = computed(() => {
    return navigations.find(nav => nav.path === route.path)?.title || ''
})

const handleLogout = () => {
    localStorage.removeItem('token')
    router.push('/login')
}

const currentLanguage = computed(() => locale.value === 'zh' ? '中文' : 'English')

const changeLanguage = (lang) => {
    locale.value = lang
    localStorage.setItem('lang', lang)
}
</script>
